mapper.select:
SELECT a.id, a.user_id, b.user_code, b.name AS user_name, a.order_id, (SELECT idt.`dict_desc` FROM info_dict idt WHERE idt.dict_type='coinType' AND idt.`dict_value` = a.coin_type) AS coin_type_name, a.coin_type, (SELECT idt.`dict_desc` FROM info_dict idt WHERE idt.dict_type='coinCreateType' AND idt.`dict_value` = a.create_type) AS create_type_name, a.create_type, a.coin_num, (SELECT c.name FROM tb_user c WHERE c.user_id = a.oper_user_id) AS oper_user_name, a.create_time FROM coin_track a JOIN tb_user b ON a.`user_id` = b.`user_id` AND b.state=1 AND b.bus_state=1 WHERE 1=1
and b.user_code = #{user_code,jdbcType=VARCHAR}
and b.name like #{user_name,jdbcType=VARCHAR}
and a.coin_type = #{coin_type,jdbcType=INTEGER}
order by a.order_id desc

SELECT c.account_bank, c.account_id, a.id, a.user_id, b.user_code, b.name AS user_name, a.create_time, ABS(a.coin_num) coin_num, a.counter_num, a.act_num, CASE apply_state WHEN 1 THEN '已打款' WHEN 0 THEN '未打款' END AS apply_state, a.serial_num FROM coin_track a JOIN tb_user b ON a.`user_id` = b.`user_id` AND b.state=1 AND b.bus_state=1 join accout_info c on a.`user_id` = c.`user_id` WHERE a.`coin_type` = 1 AND a.`create_type` = 3
and b.user_code = #{user_code,jdbcType=VARCHAR}
and a.create_time >= str_to_date(#{start_time,jdbcType=VARCHAR} '00:00:00','%Y-%m-%d %H:%i:%s')
and a.create_time <= str_to_date(#{end_time,jdbcType=VARCHAR} '23:59:59','%Y-%m-%d %H:%i:%s')
and a.apply_state = #{apply_state,jdbcType=VARCHAR}
order by a.create_time desc

mapper.update:
update coin_track set
serial_num = #{serial_num,jdbcType=VARCHAR},
apply_state = #{apply_state,jdbcType=VARCHAR},
column1 = '' where 1=1
and id = #{id,jdbcType=INTEGER}
and user_id = #{user_id,jdbcType=INTEGER}
